// @flow strict

import * as React from 'react';

import image1 from "../src/Image/bulb.jpg"
class RandomButton extends React.Component {

    constructor(props) {
        super(props)
        this.state = {

            imageList : ['cup', 'bulb', 'car'],
            number : 0

        }

    }

    randomPicker = () => {

        var randomNumber = Math.floor(Math.random() * 3);    

        this.setState({
            number : this.state.number + 1
        })
        console.log("Random Number" , this.state.number)

    }

    showPicture = () => {

        var pickednumberinState = this.state.pickedNumber
        var pickedpictureName = this.state.imageList[pickednumberinState]

        var sourceName =  "../src/Image/" + pickedpictureName + ".jpg"
        console.log(sourceName)

        return <div>
            here is image
            <img src = {sourceName}></img>
        </div>
    }


    render() {
        return (

            <div>
                <center>
                <button onClick = {this.randomPicker}> 
                    Random Picker
                </button>

                    {this.showPicture()}

                </center>
            </div>
        );
    }
}

export default RandomButton;
