// @flow strict

import * as React from 'react';



class RandomButton extends React.Component {

    constructor(props) {
        super(props)

        this.state = {

            imageList : ['cup', 'bulb', 'car'],
            pickedNumber : 0,

        }

    }

    randomPicker = () => {

        var randomNumber = Math.floor(Math.random() * 3);    


        this.setState = {

         PickedNumber : randomNumber
        }

        console.log("Random Number" , this.state.pickedNumber)

    }

    showPicture = () => {

        var pickednumberinState = this.state.pickedNumber
        var pickedpictureName = this.state.imageList[pickednumberinState]

        var sourceName = pickednumberinState + ".jpg"
        console.log(Source)

        return <div>
            <img src = {sourceName}></img>
        </div>
    }


    render() {
        return (

            <div>
                <center>
                <button onClick = {this.randomPicker}> 
                    Random Picker
                </button>



                </center>
            </div>
        );
    }
}

export default RandomButton;
